class Base{constructor(t=""){this.placeholder=t,this.data=this.placeholder,this.type="base",this.createUUID()}createUUID(){this.id="xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx".replace(/[xy]/g,t=>{const e=16*Math.random()|0;return("x"===t?e:3&e|8).toString(16)})}save(t){return this.deleteInput(t),{data:this.data,id:this.id,placeholder:this.placeholder,type:this.type}}createFromData(t){Object.keys(t).forEach(e=>{this[e]=t[e]})}}class ShortText extends Base{constructor(t){super(t),this.type="shortText"}prepareInput(t){const e=t.getElementById(this.id);e.contentEditable=!0,e.contenteditable=!0,e.classList.add("input-outline")}save(t){const e=t.getElementById(this.id);return this.data=e.innerText,super.save(t)}deleteInput(t){const e=t.getElementById(this.id);e.contentEditable=!1,e.contenteditable=!1,e.classList.remove("input-outline")}}export{Base as B,ShortText as S};
